<?php
header('Content-type: image/png');
$data = isset($_GET['data']) ? $_GET['data'] : '';
$size = isset($_GET['size']) ? $_GET['size'] : '100x100';

$jeedomLogo = "";
$mx = "";
$hny= "";
$stv= "";

$month = date('n');
$day = date('j');
$logo = (($month == 12 && $day >= 18) && ($month == 12 && $day <= 25)) ? $mx : $jeedomLogo;
$logo = ($month == 12 && $day == 31) ? $hny : $logo;
$logo = ($month == 2 && $day == 14) ? $stv : $logo;

$QR = imagecreatefrompng('http://chart.apis.google.com/chart?cht=qr&chld=H|0&chs='.$size.'&chl='.urlencode($data));
$logo = imagecreatefromstring(base64_decode($logo));

$QR_width = imagesx($QR);
$QR_height = imagesy($QR);

$jeedomLogo_width = imagesx($logo);
$jeedomLogo_height = imagesy($logo);

$logo_qr_width = $QR_width/3;
$scale = $jeedomLogo_width/$logo_qr_width;
$logo_qr_height = $jeedomLogo_height/$scale;

imagecopyresampled($QR, $logo, $QR_width/3, $QR_height/3, 0, 0, $logo_qr_width, $logo_qr_height, $jeedomLogo_width, $jeedomLogo_height);

imagepng($QR);
imagedestroy($QR);
?>
